sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	// Add your code herevar gr = new GlideRecord("x_snc_appathon_app_youtube_videos_tracked");
//gr.addEncodedQuery("campaign.start_date<=javascript:gs.endOfToday()^campaign.end_date>=javascript:gs.beginningOfToday()^campaign.active=true^remove_tracking=false");
//gr.query();
//while(gr.next()){
	var videoResult = new sn_ws.RESTMessageV2("Youtube User Analytics","GET Youtube Comments");
	videoResult.setStringParameterNoEscape("id", current.video_id);
	videoResult.setStringParameterNoEscape("maxRes",5);
	videoResult.setStringParameterNoEscape("order","relevance");
	var response = videoResult.execute();
    var responseBody = response.getBody();
	var jsonBody = JSON.parse(responseBody);
	for(var i = 0; i < jsonBody.pageInfo.totalResults; i++){
		try{
		var insertInfo = new GlideRecord("x_snc_appathon_app_youtube_video_comments");
			insertInfo.initialize();
			insertInfo.video = gr.sys_id;
			insertInfo.comment_id = jsonBody.items[i].snippet.topLevelComment.id;
			insertInfo.youtube_comment = jsonBody.items[i].snippet.topLevelComment.snippet.textDisplay;
			insertInfo.like_count = jsonBody.items[i].snippet.topLevelComment.snippet.likeCount;
			insertInfo.reply_count = jsonBody.items[i].snippet.totalReplyCount;
			var fixedDate = jsonBody.items[i].snippet.topLevelComment.snippet.publishedAt.replace("T"," ");
			insertInfo.comment_date_time = new GlideDateTime(fixedDate);
			insertInfo.insert();
		}
		catch(e){

		}
	}
//}

})(current, previous);
