sys_ux_client_script.script:
/**
* @param {params} params
* @param {api} params.api
* @param {any} params.event
* @param {any} params.imports
* @param {ApiHelpers} params.helpers
*/
function handler({api, event, helpers, imports}) {
    //set text filter presence
    api.setState("textFilterPresence", api.state.selectedCampaign ? 'presence-available-outline' : null);

    const appliedFilters = [{
        "order": 0,
        "filterId": "", //We can leave this empty
        "type": "query",
        "label": "Campaign",
        "apply_to": [
            "x_snc_appathon_app_youtube_video_comments" //More tables can be added here
        ],
        //"encoded_query": api.state.selectedCampaign ? `video.campaign.sys_id=${api.state.selectedCampaign}` : null, //If you have applied a default filter on your visualization component then add that here instead of null.
        "encoded_query": `video.campaign.sys_id=${api.state.selectedCampaign}`,
        "extraValues": {},
        "include_unmatched_or_empty": false
    }];

    const processedFilter = imports['global.mergePARFilters']()(api.state.parFilters, appliedFilters);
    api.setState("parFilters", processedFilter);
}
