sys_ux_client_script.script:
/**
* @param {params} params
* @param {api} params.api
* @param {any} params.imports
*/
function evaluateProperty({api}) {
var arr = [];

for(var i = 0; i<api.data.look_up_records_1.results.length; i++)
{
var myObj = {};
  myObj.id = api.data.look_up_records_1.results[i].sys_id.value;
myObj.label = api.data.look_up_records_1.results[i].campaign_name.value;

arr.push(myObj);

}
return arr;
}
