sysauto_script.script:
var gr = new GlideRecord("x_snc_appathon_app_twitter_mentions");
gr.addEncodedQuery("sentiment=na");
gr.query();
while(gr.next()){
	try{var videoResult = new sn_ws.RESTMessageV2("Youtube User Analytics","POST Sentiment");
	videoResult.setStringParameterNoEscape("content", gr.mention);
	var response = videoResult.execute();
    var responseBody = response.getBody();
	var jsonBody = JSON.parse(responseBody);
	gr.sentiment_score = jsonBody.documentSentiment.score;
	if(jsonBody.documentSentiment.score > 0.3){
		gr.sentiment = "positive";
	}
	else if (jsonBody.documentSentiment.score < -0.3){
		gr.sentiment = "negative";
	}
	else{
		gr.sentiment = "neutral";
	}
	gr.update();}
	catch (e){

	}
}
