sysauto_script.script:
var gr = new GlideRecord("x_snc_appathon_app_youtube_videos_tracked");
gr.addEncodedQuery("campaign.start_date<=javascript:gs.endOfToday()^campaign.end_date>=javascript:gs.beginningOfToday()^campaign.active=true^remove_tracking=false");
gr.query();
while(gr.next()){
	var videoResult = new sn_ws.RESTMessageV2("Youtube User Analytics","GET Video Info");
	videoResult.setStringParameterNoEscape("id", gr.video_id);
	var response = videoResult.execute();
    var responseBody = response.getBody();
	var jsonBody = JSON.parse(responseBody);
	var insertInfo = new GlideRecord("x_snc_appathon_app_youtube_video_statistics");
		insertInfo.initialize();
		insertInfo.video = gr.sys_id;
		insertInfo.view_count = jsonBody.items[0].statistics.viewCount;
		insertInfo.favorite_count = jsonBody.items[0].statistics.favoriteCount;
		insertInfo.like_count = jsonBody.items[0].statistics.likeCount;
		insertInfo.comment_count = jsonBody.items[0].statistics.commentCount;
		insertInfo.insert();
}
